SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;attention, ne confondez pas l'enquete cas temoin avec l'enquete de cohorte retrospective&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans les etudes cas-temoin, on recrute un groupe de sujets atteint de la maladie etudiee et un groupe de sujet indemnes de la maladie etudiees : l'information recueillie et comparee entre les 2 groupes est l'exposition.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; dans les etudes de cohorte, meme retrospective, on recrute un groupe de sujets exposes au facteur etudie et un groupe de sujets non expose a ce facteur : l'information recueillie et comparee entre les 2 groupes est la survenue de la maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au besoin, reportez vous a nouveau au tableau de la diapo 13.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
