SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une propriete interessante de l'odds ratio est que l'odds ratio d'exposition est egal a l'odds ratio de maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet, si on calcule maintenant l'odds ratio de maladie chez les exposes et les non-exposes on retrouve le meme resultat, c'est-a-dire ad/bc que sur la diapo precedente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'odds de maladie chez les exposes est egal a a sur b&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'odds de maladie chez les non-exposes est egal a  c sur d.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rapport des 2 odds est aussi egal a a d sur b c.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est une propriete de l'odds ratio : l'odds ratio d'exposition est egal a l'odds ratio de maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans une enquete cas temoin, on peut estimer l'odds ratio de maladie par l'intermediaire de l'odds ratio d'exposition.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
