SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'odds ratio estime dans une etude cas-temoins est une estimation ponctuelle realisee sur un echantillon de sujets.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette valeur calculee sur un echantillon est probablement proche mais pas exactement egale a la valeur de l'odds ratio qui aurait ete calculee sur la population dont est issu cet echantillon. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cet ecart resulte des fluctuations d'echantillonnage (c'est-a-dire du hasard).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette estimation ponctuelle doit etre encadree d'un intervalle de confiance, le plus souvent a 95%.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs formules approchees permettent de calculer les bornes inferieure et superieure de l'intervalle de confiance d'un odds ratio. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la formule donnee ici n'est pas a savoir pour l'examen.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;z&lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt; est la valeur lue sur la table de la loi normale centree reduite correspondant a &lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour un intervalle de confiance a 95%, &lt;/span&gt;&lt;span &gt;α&lt;/span&gt;&lt;span &gt; est egal a 5% ou 0,05, la valeur correspondante de z est 1,96.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
