SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les avantages des etudes de cohorte et des etudes cas-temoins sont en miroir.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les etudes de cohorte :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'etudier des exposition rares, en recrutant les sujets exposes dans les lieux ou ils sont les plus nombreux : par exemple, en milieu professionnel (rappelez vous l'exemple portant sur les salaries de l'entreprise de chantier naval sur la diapo 18).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'etudier le risque de plusieurs maladies a la fois&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'evaluer tres precisement les niveaux d'exposition lorsqu'elles sont prospectives&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent de s'assurer que l'exposition a precede l'apparition de la maladie (le respect de la sequence temporelle)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'estimer directement le risque relatif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'oppose, les enquetes cas temoins :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'etudier des maladies rares en recrutant les cas dans les lieux ou ils sont les plus nombreux : par exemple en service specialise a l'hopital&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'etudier plusieurs expositions a la fois&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettent d'etudier des pathologie dont la phase d'induction est longue, comme le cancer&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ne necessitent pas de suivre les sujets&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ont un cout modere (du fait de cette absence de suivi).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
