SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;examinons quelques exemples de determinants de sante lie a l'environnement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tout d'abord, la qualite de l'air.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut juste rappeler que l'air est indispensable a la vie. chaque jour, 15000 litres d'air transitent par les voies respiratoires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de ce fait, la preservation de la qualite de l'air est un enjeu de sante publique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les principaux polluants atmospheriques proviennent du trafic routier, du chauffage domestique, des activites industrielles, et de certaines pratiques agricoles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe aussi des sources naturelles de pollution atmospherique : les eruptions volcaniques…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
