SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontweight='bold'&gt;les polluants traceurs mesures par les reseaux de surveillance incluent :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt; dioxyde de soufre (so&lt;/span&gt;&lt;span  fontweight='bold'&gt;2&lt;/span&gt;&lt;span  fontweight='bold'&gt;)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt; fumees noires (pm&lt;/span&gt;&lt;span  fontweight='bold'&gt;10&lt;/span&gt;&lt;span  fontweight='bold'&gt; : particules de moins de 10 µ)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt; dioxyde d'azote (no&lt;/span&gt;&lt;span  fontweight='bold'&gt;2&lt;/span&gt;&lt;span  fontweight='bold'&gt;)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt; ozone (o&lt;/span&gt;&lt;span  fontweight='bold'&gt;3&lt;/span&gt;&lt;span  fontweight='bold'&gt;)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
