SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la meme etude montrait une augmentation du risque d'hospitalisation pour cause cardiovasculaire associee a l'augmentation de 10 µg/m&lt;/span&gt;&lt;span &gt;3&lt;/span&gt;&lt;span &gt; du niveau journalier des particules en suspension et du dioxyde d'azote. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une augmentation de 10 µg/m3 de la concentration des particules de moins de 10 microns et du dioxyde d'azote etait associee a un risque relatif superieur a 1 et dont les bornes de l'intervalle de confiance a 95% indiquees entre parentheses ne contenaient pas la valeur 1.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, cette etude ne mettait pas en evidence d'association statistiquement significative entre le risque d'hospitalisation pour cause cardiovasculaire et l'augmentation de 10 µg/m&lt;/span&gt;&lt;span &gt;3&lt;/span&gt;&lt;span &gt; du niveau journalier d'ozone. les bornes de l'intervalle de confiance de confiance a 95% contenaient la valeur neutre 1.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
