SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le corps humain est compose de 60% d'eau. un adulte consomme au moins  1,5 litre d'eau quotidiennement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;jusqu'au debut du 20&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; siecle, de graves epidemies de cholera notamment liees a la contamination de l'eau consommee par la population se sont produites en europe occidentale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les exigences en matiere de qualite de l'eau de consommation sont fixees par une directive europeenne transposee dans le code de la sante publique. elles sont basees sur les recommandations de l'oms.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en france, ces parametres sont surveilles par la direction departementale des affaires sanitaires et sociales en plus de la surveillance effectuee par le distribueur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette surveillance porte sur :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les caracteristiques  physico-chimiques (pesticides, nitrates)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les caracteristiques microbiologiques (bacteries traceuses). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les epidemies d'origine hydrique sont rares en france (en moyenne, une epidemie par an).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;globalement, &lt;/span&gt;&lt;span &gt;la qualite de l'eau de consommation est satisfaisante au regard des normes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
