SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on englobe sous le terme de biologie humaine les facteurs caracterisant la structure biologique de l'individu influant sur sa sante physique et mentale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit de facteurs individuels et innes sur lesquels l'individu n'exerce qu'un controle reduit et auxquels la recherche biomedicale consacre la plus grande partie de ses efforts.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les determinants biologiques de la sante englobent le patrimoine genetique individuel et la physiologie du corps humain.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de nombreuses pathologies sont lies aux determinants genetiques et physiologiques : le diabete insulino-dependant, le cancer, certaines pathologies psychiatriques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
