SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est usuel de distinguer parmi les habitudes de vie, ce qui releve :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- des comportements a risque (consommation de substances psycho-actives, violence, comportements sexuels a risque)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- du controle de facteurs de risque requerant une participation active des sujets : par exemple controle des facteurs de risque cardiovasculaire (exercice physique, nutrition) ou adoption de comportements de prevention primaire (vaccination) ou secondaire (depistage de cancers).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
