SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;envisageons rapidement deux comportements a risque : la consommation d'alcool et le tabagisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la consommation d'alcool est a l'origine d'une part important de la morbidite :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cancers&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;maladies chroniques du foie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pathologies psychiatriques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sequelles d'accidents&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la france fait partie des pays de l'union europeenne les plus consommateurs d'alcool.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la quantite d'alcool pur consommee par habitant de plus de 15 ans en france diminue regulierement depuis 40 ans. cette tendance est liee a la baisse de la consommation de vin.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;environ un tiers des personnes de 18 ans et plus ont une consommation d'alcool consideree a risque :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;alcoolisation excessive ponctuelle pour les trois quarts d'entre eux (ce sont plutot des hommes de 18 a 44 ans)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;alcoolisation excessive chronique pour un quart d'entre eux (ce sont plutot les hommes de plus de 44 ans)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vous rappelle qu'il existe de fortes disparites de la consommation d'alcool, en fonction de la categorie socioprofessionnelle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
