SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la consommation de tabac est a l'origine d'un nombre tres eleve de cancers, notamment bronchique et des voies aerodigestives superieures.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces 2 cancers sont a l'origine de 30% de la mortalite avant 65 ans (52% de la mortalite avant 65 ans si on ne considere que les hommes).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le tabagisme est responsable de 66000 deces par an en france.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;deux sources de donnees sont disponibles sur la consommation de tabac en france : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les donnees de ventes (sur la figure de gauche) et les donnees des enquetes par sondages (sur la figure de droite).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
