SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les enquetes par sondage sur des echantillons tires en population generale montrent une progression du tabagisme feminin entre le milieu des annees 1960 jusqu'au milieu des annees 1980.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela s'est traduit par un accroissement de 100% du taux de deces par cancer bronchique chez les femmes de moins de 65 ans entre 1990 et 2004 alors que ce taux a diminue de 14% chez les hommes dans la meme periode.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en 2005, on estimait que 23% des femmes et 30% des hommes de 15 a 75 ans fumaient tous les jours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'adolescence, les habitudes de consommation des filles et des garcons sont peu differenciees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en 2005, l'usage quotidien chez les filles de 17 ans etait de 32% versus 34% chez les garcons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
