SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la quantite de l'offre de soins couvre la demographie des professions medicales et paramedicales, le nombre d'etablissements et de lits, et le nombre d'equipements lourds (scanners).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la qualite de l'offre de soins designe le niveau de specialisation des soins s'etendant des soins de base qu'on appelle aussi soins primaires et les soins hautement specialises qu'on appelle soins tertiaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
