SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'accessibilite designe non seulement l'accessibilite physique (c'est-a-dire la repartition de l'offre de soins sur le territoire) mais aussi l'accessibilite economique et sociale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'accessibilite economique  designe le fait que le montant des prestations de soins ne constitue pas un frein au recours aux soins. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'accessibilite sociale designe le fait que la position sociale d'un individu ne constitue pas un obstacle au recours aux soins. par exemple, on s'est apercu qu'en depit de la gratuite du depistage generalise du cancer du sein, il existait de fortes disparites de la participation aux campagnes. des observations similaires ont ete faites pour les soins curatifs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@v. organisation des soins@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
