SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide44.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'efficacite de l'offre de soins est sa capacite a produire les effets attendus : sur le plan clinique (reduction de la mortalite), sur le plan fonctionnel (recuperation, sequelles), sur le plan de la qualite de vie &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cout se refere a la dimension economique, et pas seulement comptable, de l'offre de soins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'une maniere caricaturale, des auteurs ont montre qu'en adoptant une approche comptable la reduction de la mortalite prematuree evitable obtenue grace a la prevention du tabagisme s'accompagnait d'une augmentation des depenses de soins liee a l'accroissement de la population aux ages avances.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce raisonnement qui consiste a reduire l'analyse des couts a l'equilibre a court terme des comptes de l'assurance maladie et reduire l'interet collectif aux comptes d'un agent particulier (l'assurance maladie) est naif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il ignore la valeur economique et sociale (au sens du bien etre individuel et collectif) d'un allongement de l'esperance de vie. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rapport efficacite sur cout economique est l'efficience de l'offre de soins. dans un contexte de ressources limitees, l'offre de soins doit etre la plus efficace possible au moindre cout.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
