SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il s'agit d'une etude epidemiologique etiologique ou analytique comparant l'incidence cumulee d'un evenement de sante (le deces par fievre puerperale) entre 2 cohortes de femmes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chaque cohorte est definie par le pavillon ou a eu lieu l'accouchement. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le cas d'une etude epidemiologique de type cas-temoin, le recrutement aurait ete realise sur l'evenement de sante, c'est-a-dire le deces maternel par fievre puerperale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on aurait compare la frequence d'accouchement dans le pavillon klin entre les cas, c'est-a-dire les meres decedees par fievre puerperale, et les temoins, c' est-a-dire les meres vivantes ou plus precisement n'ayant pas decede par fievre puerperale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
