SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;seule la force de l'association, suggeree par un risque relatif de 0,35 correspondant a une reduction par 3 du risque de deces, figurait dans l'enonce relatif a la seconde etude.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la constance de l'association est un critere de causalite externe, qui plus est non mentionne dans l'enonce.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la relation de type dose-effet est un critere de causalite interne, mais rien n'indique dans l'enonce l'existence de cette relation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, les fluctuations d'echantillonnage designent l'erreur aleatoire, c'es-a-dire due au hasard, dans l'estimation d'un parametre. il ne s'agit aucunement d'un critere de causalite dans l'interpretation d'une association.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
