SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la reponse attendue a la question 7 est abd.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'association statistiquement significative mise en evidence dans la seconde etude est un argument en faveur :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de la reduction du risque de deces par fievre puerperale par la desinfection soigneuse des mains des etudiants en medecine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de la transmission par les mains des etudiants en medecine de l'agent responsable de la fievre puerperale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de l'existence d'une hygiene des mains defectueuse chez les etudiants en medecine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, cette seconde etude suggere que l'hygiene des mains suffit a reduire le risque de deces par fievre puerperale et n'indique pas qu'il soit necessaire de supprimer les travaux anatomiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
