SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans les annees 1970,  l'attention des pouvoirs publics a ete attiree par les risques lies aux produits de sante, d'abord les risques lies aux medicaments, puis aux produits sanguins et aux dispositifs medicaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans les annees 1990, la notion d'infection nosocomiale s'est developpee et la lutte contre les infections nosocomiales s'est structuree.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on reviendra au cours de ce chapitre sur la notion d'infection nosocomiales&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
