SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces estimations de 14.000 a 98.000 deces annuels ont ete obtenues par extrapolation a l'ensemble du territoire americain des resultats d'etudes epidemiologiques conduites en californie et dans l'etat de new york dans les annees 1980 et un peu plus tard en utah et au colorado.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces etudes ont permis de :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- dresser une typologie des risques iatrogenes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- d'estimer le taux d'incidence des evenements iatrogenes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- d'estimer la proportion d'evenements evitables&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- d'identifier les facteurs de risque associes aux evenements iatrogenes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- d'evaluer les consequences des evenement iatrogenes sur le plan humain et les couts qu'ils engendraient.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
