SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les resultats publies dans ce rapport ont ete tres mediatises, avec une course aux effets d'annonce, meme dans les journaux les plus serieux comme le montrent ces extraits :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du washington post qui relaie que les erreurs medicales constitueraient la 5&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; cause de deces&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ou new york times qui compare cette mortalite hospitaliere indue a 3 boeing 747 qui s'ecraseraient tous les 2 jours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
