SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cependant, des medecins ont critique les resultats de ce rapport, en mettant en cause la methodologie utilisee pour juger le caractere evitable ou non des evenements iatrogenes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le caractere evitable d'un evenement etait base sur le jugement d'experts a partir de la revue du dossier medical et il etait reproche a ce jugement d'experts d'etre subjectif et peu reproductible.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
