SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;parallelement a l'evolution du concept de risque iatrogene, plusieurs definitions se sont succedees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette seconde partie est consacree a la definition utilisee actuellement pour qualifier le risque iatrogene en france. on verra notamment 3 caracteristiques que sont la gravite, l'evitabilite, et la criticite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
