SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est possible en fonction du caractere evitable ou inevitable de l'evenement indesirable de dresser une typologie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en presence d'une erreur dans le processus de soin, s'il survient un evenement indesirable, on parlera d'evenement indesirable evitable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;toujours en presence d'une erreur, si le resultat est conforme a ce qui est attendu de la prise en charge, c'est-a-dire qu'il n'ya pas eu d'evenement indesirable pour le patient, on parlera d'evenement indesirable potentiel.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en absence d'erreur lors de l'action de soins, s'il survient un evenement indesirable, on le qualifiera d'inevitable. il s'agit de l'alea therapeutique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
