SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;outre la gravite et l'evitabilite d'un ei, une derniere caracteristique est sa criticite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la criticite d'un evenement indesirable est le produit de sa frequence par sa gravite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe des echelles de criticite. celle de bristol reproduite ici en est un exemple dont le contenu n'est pas a connaitre pour l'examen.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
