SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'enquete eneis a tout d'abord recense les sejours hospitaliers causes par un evenement indesirable grave lie aux soins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans cette enquete, un evenement indesirable grave etait a l'origine de pres de 4% de l'ensemble des sejours hospitaliers.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette diapositive montre la repartition des hospitalisations causees par un evenement indesirable grave et par un evenement indesirable grave evitable&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
