SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si l'enquete eneis a ete realisee en etablissements de sante, il ne faut pas perdre de vue que les evenements indesirables graves associes aux soins concernent aussi la medecine de ville.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les deux tiers des hospitalisations liees a un evenements indesirables graves etaient imputables a une prise en charge en medecine de ville, le reste etant consecutif a une hospitalisation anterieure.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un evenement indesirable grave evitable etait a l'origine de pres de 1.8% de l'ensemble des sejours hospitaliers soit 46% des  hospitalisations motivees par un evenement indesirable grave. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
