SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'enquete eneis s'est egalement interessee aux evenements indesirables graves survenus en cours d'hospitalisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'estimation ponctuelle du taux d'incidence des evenements indesirables graves survenus en cours de sejour etait de 6,6 pour 1000 personne journees d'hospitalisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'estimation du taux d'incidence des evenements indesirables graves evitables etait de 2,3 pour 1000 journees d'hospitalisation, soit 35% de la totalite des evenements indesirables graves survenus en cours de sejour.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les evenements indesirables graves les plus frequents etaient ceux lies aux actes invasifs (interventions chirurgicales, anesthesies, actes de radiologie interventionnelle, endoscopies, biopsies, ponctions, pose de catheters centraux, et  intubations).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour chaque colonne, la somme est superieure au total, car un patient peut avoir des eig de differentes causes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en faisant toujours les memes reserves que precedemment sur ce type d'extrapolation, on peut estimer que le nombre d'eig survenus pendant une hospitalisation se situe dans une fourchette de 350 000 a 460 000 par an,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dont 120 000 a 190 000 eig peuvent etre consideres comme evitables.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
