SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en revanche, il faut detailler les risques d'infection associee aux soins car ils ne seront pas abordes par ailleurs alors qu'ils etaient a l'origine d'une part non negligeable des evenements indesirables graves observes dans l'enquete eneis.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
