SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les facteurs associes a une prevalence plus elevee etaient l'age de plus de 65 ans, le sexe masculin, la presence de&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comorbidites impliquant le deces dans les cinq ans, l'immunodepression, le catheterisme vasculaire, le sondage urinaire et l'intubation tracheale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;1 351 etablissements de sante ont participe aux deux enquetes de prevalence de 2001 et 2006.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;entre ces deux enquetes,  la prevalence des patients infectes dans ces etablissements a diminue de 4,61% a 4,25 % (soit une diminution relative de 8%).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
