SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette figure empruntee represente imparfaitement la multiplicite et l'intrication des risques en etablissements de sante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;certains risques sont specifiques des etablissements de sante (ce sont les risques d'evenements indesirables evitables ou non lies aux soins).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'autres risques sont communs a toutes les organisations comme les risques techniques (incendie, panne d'electricite), environnementaux (legionellose), mais aussi les risques sociaux (absenteisme), informatiques ou financiers.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils peuvent mettre en jeu la continuite des soins et la securite des personnes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de plus, il existe un recoupement des risques qui concernent les patients et des risques qui concernent les professionnels.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le contenu de cette figure n'est pas a connaitre pour l'examen.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
