SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans le langage courant, le risque est un terme de sens commun variable mais qui associe toujours la notion de danger et la notion d'alea.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour les scientifiques, le concept de risque n'est pas univoque et varie en fonction des disciplines (mathematique, finance, technologie, psychologie). ces definitions sont indicatives et ne sont pas a connaitre pour l'epreuve de ssh.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
