SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une definition simple du risque en sante publique est la probabilite de survenue d'un evenement attribuable a un danger pour les individus d'une population. le risque est une notion quantitative et probabiliste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le risque est une notion pertinent au niveau d'une population.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le risque est a distinguer du danger.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le danger est une notion qualitative : c'est la propriete intrinseque de nuire a la sante que possede un agent physique, chimique, ou biologique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le danger est une menace potentielle pouvant donner lieu a un risque sanitaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le risque sanitaire est la concretisation d'un danger, mais tous les dangers ne constituent pas des risques sanitaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a titre d'exemple, le tabac est un danger, qui donne lieu, via le tabagisme, a un risque de cancer bronchique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il peut arriver aussi qu'un danger soit a l'origine d'un risque sanitaire non-identifie faute de consequence visible ou de surveillance (par exemple, le risque d'encephalopathie subaigue spongiforme transmissible liee au danger constitue par l'encephalopathie spongiforme bovine).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut noter qu'il existe une perception sociale du risque faisant qu'un danger peut etre percu a tort comme un risque important alors que de nombreux risques ne sont pas percus comme dangereux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
