SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en epidemiologie descriptive, les mesures qui caracterisent la distribution des etats de sante dans les populations englobent :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les mesures de risque : c'est-a-dire la probabilite d'etre ou de devenir malade (ou de deceder)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les mesures d'incidence qui indiquent la vitesse d'apparition des nouveaux cas de la maladie (on parle de taux d'incidence) ou la vitesse d'apparition des deces (on parle de taux de mortalite).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
