SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide33.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le risque de maladie est la probabilite de devenir malade au cours d'un intervalle fixe.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme toute probabilite, le risque de maladie est compris entre 0 et 1. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans une population fermee (c'est-a-dire, sans entree ni sortie de sujets pendant l'intervalle), le risque de maladie est egal au rapport de l'effectif de nouveaux cas de maladie apparus dans un intervalle de temps divise par l'effectif de sujets non-malades au debut de cet intervalle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iii.4. risque de maladie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide34.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans une population ouverte (c'est-a-dire, avec entree ou sortie de sujets au cours de l'intervalle), le risque de maladie pendant l'intervalle de temps est egal a 1 moins l'inverse de l'exponentielle du taux d'incidence multiplie par la duree de l'intervalle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette formule n'est valable que si le taux d'incidence est constant au cours de l'intervalle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si l'intervalle est long, le taux d'incidence augmente probablement avec l'age des sujets. dans ce cas, on decoupe l'intervalle en intervalles plus petits sur lesquels le taux d'incidence est constant.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
