SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide35.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lorsqu'on s'interesse non plus a la survenue d'une maladie mais a la survenue du deces, on parle de mortalite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le taux brut de mortalite est le taux d'incidence des deces.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il se calcule de la meme facon que le taux d'incidence d'une maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le taux de mortalite s'exprime en nombre de deces par personnes annees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iii.5. taux de mortalite@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide36.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il arrive souvent qu'on ne puisse pas calculer precisement l'effectif de personnes temps dans l'intervalle [t, t+ &lt;/span&gt;&lt;span  fontweight='bold'&gt;Δ&lt;/span&gt;&lt;span  fontweight='bold'&gt;t[&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sous l'hypothese que les deces se repartissent uniformement au cours de cet intervalle [t, t+ &lt;/span&gt;&lt;span  fontweight='bold'&gt;Δ&lt;/span&gt;&lt;span  fontweight='bold'&gt;t[&lt;/span&gt;&lt;span &gt;, on approxime l'effectif de pt par la moyenne de l'effectif de sujets vivants en debut et fin d'intervalle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iii.5. taux de mortalite@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide37.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lorsqu'on s'interesse a une cause particuliere de deces, le taux brut de mortalite pour cette cause est egale au nombre de deces dus a cette cause pendant un intervalle de temps divise par l'effectif de personnes temps susceptibles de deceder pendant cet intervalle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il ne faut pas confondre la  notion de taux brut de mortalite due a une cause de deces avec le taux de mortalite specifique d'un sous-groupe.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iii.5. taux de mortalite@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide38.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la mortalite peut egalement etre etudiee dans differents sous-groupes de la population.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les taux de mortalite calcules par sexe, par classe d'age, ou par categorie socio-professionnelles sont des taux specifiques de mortalite .&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on calculera par exemple le taux specifique de mortalite des hommes de la classe d'age 55-64 ans.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
