// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iv.1. standardisation directe@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide43.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la standardisation directe, aussi appelee methode de la population type, consiste a calculer un taux standardise de mortalite en ponderant les taux specifique de mortalite pour chaque classe d'age par la structure d'age d'une population de reference.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la structure d'age de la population de reference, peut etre :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;celle de la population nationale lorsqu'on compare la mortalite de deux regions&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;celle de la population europeenne lorsqu'on compare la mortalite de deux pays&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;celle de la population mondiale lorsqu'on compare la mortalite de deux pays&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iv.2. standardisation indirecte@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide44.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la standardisation indirecte, aussi appelee methode de la mortalite type, consiste a calculer le rapport du nombre de deces observes sur le nombre de deces attendus si on appliquait les taux specifique de mortalite d'une population de reference.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce rapport est designe dans la litterature internationale par le smr pour standardized mortality ratio.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nombre de deces attendus est calcule par le produit pour chaque classe d'age du taux specifique de mortalite dans la population de reference par l'effectif de cette classe d'age dans la population observee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un smr superieur a 1 indique une surmortalite dans la population observee par rapport a la population de reference.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un smr inferieur a 1 indique une sous-mortalite dans la population observee par rapport a la population de reference.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

