SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le taux brut de mortalite du limousin etait donne dans la derniere ligne du tableau. il est egal a 13,4 deces pour 1000 personnes annees. il n'etait pas necessaire de le calculer. neanmoins, ce taux a ete obtenu en divisant l'effectif total de deces par l'effectif total de personnes temps presentes en 1996.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de la meme facon, on pouvait lire le taux brut de mortalite de la region nord pas-de-calais directement sur la derniere ligne du tableau. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
