SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on observe qu'en 1996, le taux brut de mortalite etait plus eleve dans le limousin que dans le nord pas de calais. qu'est-ce qui est susceptible d'expliquer cette difference ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
