SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le ratio standardise de mortalite est le ratio de l'effectif de deces observe dans la region limousin sur l'effectif de deces attendu avec la mortalite type.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'effectif de deces observe se lit directement dans le tableau : c'est l'effectif total de deces observe dans le limousin en 1996, soit 9723.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'effectif de deces attendu doit etre calcule.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
