SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le ratio standardise de mortalite de la region limousin etait egal a 1,05 en 1996.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce smr est superieur a 1, ce qui denote un exces de mortalite dans le limousin comparativement a la france, apres s'etre affranchi de la structure d'age de cette region.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous trouverez dans l'exercice ii du chapitre ii le smr de la region nord pas-de-calais et son interpretation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
