SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les mesures utilisees en epidemiologie sont des rapports : des proportions, des ratios, des odds (ou cote), des indices, et des taux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;1. dans une proportion, le numerateur est inclus dans le denominateur.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le numerateur et le denominateur sont de meme nature.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une proportion est comprise entre 0 et 1 (ou 0 et 100% si on s'exprime en pourcentage).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans un echantillon de 50 patients, 30 sont de sexe masculin. la proportion d'hommes est de 60%. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;2. un ratio est le rapport des effectifs de 2 classes d'une meme variable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le numerateur et le denominateur sont de meme nature, mais sont exclusifs (c'est-a-dire que le numerateur n'est pas contenu dans le denominateur).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un ratio s'exprime par un nombre sans unite, compris entre 0 et plus l'infini.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans l'exemple precedent, le ratio homme sur femme (sex ratio) est egal a 1,5.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
