SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;3. la cote ou odds est le ratio de la probabilite de survenue d'un evenement sur la probabilite de non survenue de cet evenement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est un ratio.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sa valeur est comprise entre compris entre 0 et plus l'infini.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'investigation d'une epidemie montre que 80 des 100 malades ont consomme un aliment. l'odds d'exposition a cet aliment est de 0,8 divise par (1 - 0,8), soit 4.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;4. un indice est le rapport de 2 effectifs de nature differente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, le nombre de lits par medecin dans un hopital.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;5. en epidemiologie, un taux est un rapport qui integre le temps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le numerateur est l'effectif de sujets presentant l'evenement pendant un intervalle de temps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le denominateur est l'effectif de sujets susceptible de presenter l'evenement pendant cet intervalle de temps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un sujet qui decede au milieu de cet intervalle de temps n'a ete susceptible de presenter l'evenement que pendant la moitie de l'intervalle. il ne participe au denominateur que pour la moitie de l'intervalle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le denominateur est exprime en personne temps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le taux est un nombre compris entre 0 et plus l'infini. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des exemples seront donnes lorsqu'on abordera le taux d'incidence.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
