// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@invasion@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;rappels :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le developpement du placenta debute durant l'implantation, en meme temps que se met en place la reaction deciduale. en particulier a partir de la phase d'invasion (j7). quand le blastocyste adhere a l'endothelium endometrial, les cellules trophoblastiques se differencient en une couche interne de cytotrophoblastes et une couche externe de syncytiotrophoblastes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@stade prelacunaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;alors que le cytotrophoblaste prolifere, de nouvelles cellules justes formees migrent vers le syncytiotrophoblaste et perdent leur membrane cellulaire pour former une masse polynucleaire qui se developpe rapidement. le cytotrophoblaste secrete des enzymes proteolitiques et le syncytiotrophoblaste envoie des projections digiformes permettant au blastocyste d'integrer l'endometre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@stade pre-villeux ou lacunaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt; vers j9, des lacunes commencent a se former dans le syncytiotrophoblaste. quand celui-ci va eroder les vaisseaux sanguins et les glandes, les lacunes vont se remplir de sang maternel et de secretions glandulaires. les lacunes isolees vont fusionner pour former un reseau a travers lequel le sang maternel va circuler, etablissant ainsi une circulation utero placentaire primitive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@stade villeux secondaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vers la fin de la 2e semaine de developpement, de petites projections du cytotrophoblaste commencent a s'etendre dans le syncytiotrophoblaste, formant les villosites choriales primitives. tot dans la 3e semaine, le mesoderme extra-embryonnaire grandit dans ces villosites, formant un coeur de tissu conjonctif et creant les villosites choriales secondaires. a la fin de la 3e semaine, les vaisseau sanguins embryonnaires ont commence a se former dans le mesoderme extra embryonnaire des villosites choriales secondaires, les transformant en villosites choriales ternaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

