SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;au cours de la 3&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; semaine, les cellules cytotrophoblastiques des villosites choriales ternaires croissent en direction de la decidue basale (decidua basalis) et se propage a travers elle formant une coque cytotrophoblastique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites qui sont connectees a la decidue basale par la coque trophoblastique sont appelees villosites crampons. les villosites qui se developpent depuis les cotes des tiges des villosites sont appelees les villosites flottantes (branch villi). celles-ci se trouvent dans l'espace appele chambre intervilleuse et vont servir de sites principaux d'echanges entre la mere et l'enfant.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
