SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;transformation de type epithelial des fibroblastes (cellules fusiformes ) du stroma endometrial &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les cellules vont s'agrandir et devenir polygonales, pour stocker glycogene et graisses. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce phenomene est accompagne par l'infiltration de nombreux leucocytes d'origine maternelle et par le remaniement de la matrice extracellulaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;certaines cellules deciduales apoptotiques sont phagocytees par le st et serviront de substrat nutritif a l'embryon avant l'etablissement de la circulation foeto-placentaire. (mecanisme histiotrophes)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la reaction deciduale semble trouver son origine dans le maintient prolonge de la secretion de progesterone. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;st vont eroder les cellules polygonales et permettre ainsi la resorption des nutriments qu'elles contiennent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la reaction deciduale entraine un recrutement lymphocytaire : role dans invasion trophoblastique + immunite maternelle protegeant l'embryon. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les caduques se subdivisent en deux couches : la zone compacte superficielle et la couche spongieuse dessous (=zone de fragilite qui permettra le clivage lors de l'accouchement.)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
