SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une fois les vill iiir formees a la fin de la 4&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; semaine, elles vont encore evoluer au niveau histologique et morphologique. elles vont croitre en longueur puis en largeur grace a une proliferation du trophoblaste (avec un systeme de bourgeonnement de st rapidement colonise par du ct, du mee et des cellules endotheliales.)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un systeme d'arborisation se met en place entre 9 et 16 semaines : allongement, ramification et modification en vill interm immatures ; puis en vill interm matures a partir de 26 sem, qui sont des vill plus longues et greles qui elles memes devloppent des vill terminales (diametre de 40 a 80 microns) qui se terminent par de nouveaux bourgeons trophobl.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;a la fin de la grossesse on trouvera donc dans le placenta six types de villosites:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites tertiaires mesenchymateuses generent la croissance en longueur de l'arbre villeux&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites souches assurant une &lt;/span&gt;&lt;span  fontweight='bold'&gt;stabilite mecanique&lt;/span&gt;&lt;span &gt; de l'arbre villositaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites intermediaires immatures lieu de proliferation du trophoblaste et des bourgeons trophoblastiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites intermediaires matures dont sont issues&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les villosites terminales ou libres avec leur vascularisation foetale maximale, qui constituent 40% du volume et &lt;/span&gt;&lt;span  fontweight='bold'&gt;50% de la surface d'echange&lt;/span&gt;&lt;span &gt; du placenta a terme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les bourgeons trophoblastiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
