SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;couche cellulaire interne des villosites du chorion&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;element clef du placenta&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cellules epitheliales polygonales derivees du trophectoderme, pluripotentielle&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;differenciation en st qui va permettre l'enfouissement du blastocyste dans la muqueuse uterine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;proliferation de ct qui colonise le st pour ebaucher les vc&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;etre envahies par le mesenchyme embryonnaire : arborisation vc&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;creation barriere placentaire : endothelium capillaires placentaire + mesenchyme qui les entoure + ct villeux + st&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;puis ct prolifere et migrent dans endometre (ct extra vill) pour persister dans paroi ou vers paroi des arteres spiralees et devenir endo vasculaires&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
