SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le syncytiotrophoblaste est la principale interface fœtomaternelle,  assure les fonctions d'echange :diffusion sple, transfert facilite, transfert actif et endocytose &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;csf-1 : colony stimulating factor 1.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;egf : epidermal growth factor. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lif : leukemia inhibitory factor. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;m-csf : macrophage-colony stimulating factor.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tgf&lt;/span&gt;&lt;span &gt;β1 : &lt;/span&gt;&lt;span &gt;transforming growth factor &lt;/span&gt;&lt;span &gt;β1.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
